import React, { useState } from "react";
import tasksData from "../../data/tasks";

const NewTask = ({changeState}) => {

    const [newtask,setNewTask] = useState("");

    const addNewTask = (e) => {
        setNewTask(e.target.value)
    }

    const submitHandler = (changeState) => {
        console.log(newtask)
        changeState({
            title: newtask,
            due: 1596844800
        })
    }



  return (
    <div className="App">
      <form>
        <input type="text" onChange={(e) => addNewTask(e)} />
        <button onClick={(changeState) => submitHandler(changeState)}>+</button>
      </form>
    </div>
  );
}

export default NewTask;
