import React, { Fragment, useState } from "react";
import TasksList from "../../components/TasksList";
import UserProfile from "../../components/UserProfile";
import tasksData from "../../data/tasks";

// const DragImage = React.forwardRef((props, ref) => <TaskDragImage innerRef={ref} {...props} />);



function App(props) {
  const [tasks, setTasks] = useState(tasksData);
  const [newtask,setNewTask] = useState("");

  const addNewTask = (e) => {
    setNewTask(e.target.value)
  }

  const submitHandler = (e) => {
    e.preventDefault()
    setTasks([...tasks,{title:newtask,due:1597017600}]);
    setNewTask("")
  }


  return (
      <Fragment>

      { 
        props.location.pathname === "add" ? (
          <div className="search">
        <form onSubmit={submitHandler}>
          <input type="text" onChange={(e) => addNewTask(e)} value={newtask} />
          <button>+</button>
        </form>
        </div>
        ) : (
          <div className="App">
            <UserProfile taskCount={tasks.length} profile={{ name: 'Floyd Mullins' }} />
            <TasksList tasks={tasks} />
          </div>
        )

      }



      {console.log(props.location.pathname)}

  </Fragment>

  );
}

export default App;
