import React from 'react';
import {Route,Link,Switch,BrowserRouter as Router} from "react-router-dom"

const DropButton = React.forwardRef((props, ref) => {
  const changeCallback = props.change;
  const toggleActivate = (changeCallback) => {
    changeCallback(true)
  }
  return (
      <div ref={ref} className={`drop-button ${props.className}`} onClick={changeCallback => toggleActivate(changeCallback)}>+</div>

  )
});

export default DropButton;
